function gtile(x,y)
	x=math.floor(x)
	y=math.floor(y)
	x=math.max(math.min(x,256),0)
	y=math.max(math.min(y,256),0)
	return mapTiles[x+y*256]
end

function gcolor(x,y)
	x=math.floor(x)
	y=math.floor(y)
	return mapColors[x+y*256]
end

